import Card from "./UI/Card";
import "./ExpenseItem.css"

const ExpanseItems = (props) => {

  //console.log(props.title + 'Clicked on the first time')

  const [title, setTitle] = useState(props.title)
  let clickHandler = () => {
    setTitle('Updated')
    //console.log(counter)
  }
  // console.log(title)

  var[amount, setAmount] = useState(props.amount)
  let incrementAmountHundler = () => { 
    setAmount(amount = amount+1)
    console.log(amount)
  }

  let title2 = props.title
  const testClickHundler = () => {
    title2 = 'Updated'
    console.log(title2 + ' in the function')
  }

  return (
    <Card className="expense-item">
      <div>
        <ExpenseDate date={props.date} />
      </div>
      <div className="expense-item__description">
        <h2>{title}</h2> 
        <div className="expense-item__price"> ${amount} </div>
      </div>
        <button onClick={ clickHandler } >Change Title</button>
        <button onClick={ testClickHundler } >Hindura Titire</button>
        <button onClick={incrementAmountHundler}> Increment counter </button>
    </Card>
  );
}
