//import ExpenseItems from "./expenses/ExpenseItems";
import ExpenseItems from './ExpenseItems'
import ExpensesFilter from './../filters/ExpensesFilter'

import Card from "../UI/Card";

let Expenses = (props) => {

    const filterHandler = (filteredYear) => {
      props.onAddFilter(filteredYear)
    }

  return (
    <div>
      <Card className="expenses">
        <ExpensesFilter onFilter={filterHandler}/>
        <ExpenseItems
          date={props.expenses_key[0].date}
          title={props.expenses_key[0].title}
          amount={props.expenses_key[0].amount}
        />

        <ExpenseItems
          date={props.expenses_key[1].date}
          title={props.expenses_key[1].title}
          amount={props.expenses_key[1].amount}
        />

        <ExpenseItems
          date={props.expenses_key[2].date}
          title={props.expenses_key[2].title}
          amount={props.expenses_key[2].amount}
        />

        <ExpenseItems
          date={props.expenses_key[3].date}
          title={props.expenses_key[3].title}
          amount={props.expenses_key[3].amount}
        />
      </Card>
    </div>
  );
};

export default Expenses;
